mapper.select:
SELECT * FROM VIDEO
WHERE ${key} LIKE CONCAT('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}

mapper.update:
UPDATE VIDEO
SET TITLE = #{title}, FITPARTNAME = #{fitPartName}, YOUTUBEID = #{youtubeId}, CHANNELNAME = #{channelName}
WHERE VIDEOID = #{videoId}

UPDATE VIDEO
SET VIEWCNT = VIEWCNT + 1
WHERE VIDEOID = #{videoId}
